var crypto = require('crypto');

// tag lib
module.exports.fmt = require('./tag/fmt');

// base model
module.exports.FlipFilter = require('./model/FlipFilter');

module.exports.FlipQuery = require('./model/FlipQuery');

// exception
module.exports.error = require('./exception/error');

// util
module.exports.extend = require('extend');

module.exports.util = {
	startWith: function(str, frag) {
		var reg = new RegExp("^" + frag);
		return reg.test(str);
	},
	endWith: function(str, frag) {
		var reg = new RegExp(frag + "$");
		return reg.test(str);
	},
	md5: function(str) {
		var md5sum = crypto.createHash('md5');
		md5sum.update(str);
		str = md5sum.digest('hex');
		return str;
	},
	createClass: require('./util/lang_object').createClass
}