var FlipQuery = function(query) {
	if (query) {
		var q = new FlipQuery();
		for (var key in query) {
			var val = query[key];
			if (key != '__proto__' && val.length > 0) {
				q[key] = new RegExp(val, 'i');;
			}
		}

		return q;
	}
};

